import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { login } from '../store/user/userSlice';
import { BsCheck } from 'react-icons/fa';

const Login = () => {
    const navigate = useNavigate();
    const dispatch = useDispatch()

    const loginUser=(e)=>{
        e.preventDefault();
        dispatch(login(true))
        navigate('/')
    }
    return (
        <div className='login-form'>
            <form onSubmit={(e)=>loginUser(e)}>
                 <div>
                    <div><label htmlFor='userId'>아이디</label></div>
                    <div><input type="text" id="userId"  placeholder='아이디입력' /></div>
                 </div>
                 <div>
                    <div><label htmlFor='userPass'>비밀번호</label></div>
                    <div><input type="password"  id="userPass" /></div>
                    <label for="checkbox">
                <span class="idsave">
                    <span class="idsave-checked">
                        <BsCheck />
 </span>
                </span>
                <span class="text">아이디 저장</span>
            </label>
                 </div>
                 <div>
                     <input type="submit" value="로그인"  />
                 </div>
            </form>
        </div>
    );
};

export default Login;
